--bg-color: rgb(30,123,167);
--font-size: 1rem;

figure {
  padding: 2rem 0;
}

ul {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-gap: 0.5rem;

  li {
    background: var(--bg-color);
    position: relative;
    aspect-ratio: 1 / 1;
    font-size: var(--font-size);
    text-transform: uppercase;
		font-weight: 500;
    color: white;
    text-align: center;
    letter-spacing: 1px;

    a {
      height: 100%;
      width: 100%;
      display: flex;
      justify-content: center;
      align-items: center;
      padding: 1rem;

      img {
        position: absolute;
        top: 0;
        left: 0;
        height: 100%;
        width: 100%;
        object-fit: cover;
        z-index: 0;
        transition: opacity 0.2s;
      }

      img + span {
        color: transparent;
      }

      span {
        z-index: 1;
        transition: color 0.2s;
      }

      &:hover, &:focus {
        img {
          opacity: 0.1;
        }
        span {
          color: white;
        }
      }
    }
  }
}

section {
  background: #f5f5f5;
  padding: 6rem 0;
}

.container {
  display: grid;
  grid-template-columns: 2fr 1fr;
  grid-gap: 4rem;

  .content {
    flex: 3;

    .primo-copy {
      font-family: Nunito, sans-serif;
      color: #707070;
      font-size: 17px;
      line-height: 28px;
    }
  }

  img {
    flex: 1;
    aspect-ratio: 1/1;
  }
}

section {
  padding: 4rem 0;

  .container {
    display: grid;
    grid-template-columns: 1fr;
    grid-gap: 1rem;
  }

  form{
  	text-align:left;
  }

  input,button,textarea,select{
  	padding:7px;
  	background-color:var(--bg-color-secondary);
  	width:100%;
  	box-sizing:border-box;
    border: 0;
  }

  button:focus, submit:focus{
  	outline:0;
  }

  textarea{
  	min-height:250px;
  	overflow:auto;
  }

  .button {
  	background-color:rgb(30,123,167);
  	border-radius:99px;
  	color:#ffffff;
  	border:0;
  	width:auto;
  	cursor:pointer;
  	min-width:100px;
      -webkit-appearance:none;
      font-weight:600;
  }

  /*Label*/
  label{
    font-weight:600;
    margin: 1.5rem 0;
    display: block;

    span {
      display: block;
      padding-bottom: 0.25rem;
    }
  }
}

@media (min-width: 900px) {
  section {
    .container {
      grid-template-columns: 1fr 2fr;
    	grid-gap: 3rem;
    }
