.theme-switch-wrapper {
  display: flex;
  align-items: center;
  justify-content: flex-end;

  em {
    margin-left: 10px;
    font-size: 1rem;
  } 
}
.theme-switch {
  display: inline-block;
  height: 34px;
  position: relative;
  width: 60px;
}

.theme-switch input {
  display:none;
} 

.slider {
  background-color: #111;
  bottom: 0;
  cursor: pointer;
  left: 0;
  position: absolute;
  right: 0;
  top: 0;
  transition: .4s;

  &:before {
    background-color: #eee;
    bottom: 4px;
    content: "";
    height: 26px;
    left: 4px;
    position: absolute;
    transition: .4s;
    width: 26px;
  }
}

input:checked + .slider {
  background-color: #eee;

  &:before {
    background-color: #111;
  }
}

input:checked + .slider:before {
  transform: translateX(26px);
}

.slider.round {
  border-radius: 34px;
}

.slider.round:before {
  border-radius: 50%;
}


